def ask_nbr_handle_ds_array
    puts "Combien y a-t-il de handle dans cette array ?"
    nbr_handle_ds_array = $journalistes.length
    puts "Il y a #{nbr_handle_ds_array} handle"
end    


def ask_plus_petit_handle
    puts "Quel est le handle le plus court de cette liste?"
        nbr_caractères_string = $journalistes[0].length
        $journalistes.each {
            |journ|
            if journ.length <= nbr_caractères_string
                 nbr_caractères_string = journ.length
                 $plus_petit_handle = journ
            end    
             }
    puts "Le plus petit handle est #{$plus_petit_handle}."
end    


def ask_nbr_handle_5caractères
    puts "Combien y-a-t'il de handle contenant 5 caractères (le @ ne compte pas pour un caractère)?"
        $nbr_handle_5caractères=0    
        $journalistes.select {
            |element| 
            if element.size == 6
                $nbr_handle_5caractères = $nbr_handle_5caractères + 1
            end    
            }
    puts "Il y a #{$nbr_handle_5caractères} handle de 5 caractères."
end


def ask_nbr_handle_Maj
    puts "Combien de handle commencent par une majuscule (première lettre juste après le @) ?"
        $nbr_handle_Maj = 0
        $journalistes.each {
            |element|
            if element.slice(1) =~ /[A-Z]/
                $nbr_handle_Maj = $nbr_handle_Maj + 1
            end    
        }
    puts "Il y a #{$nbr_handle_Maj} handle qui commence par une majuscule"    
end    

def ask_ordre_alphabétique
